var basePaths=basePath+'/resources/uskin/'; 
require.config({
    //相对路径为当前目录
    baseUrl: basePaths,
    paths: {

        "uplan": "js/uplan.min",
        "uskin": "js/uskin.min",

        //拖拽排序
        "dragsort": "uplanPlug/jquerydragsort/jquery.dragsort.min",

        //时间控件
        "datetimepicker": "uplanPlug/datetimepicker/js/uplan.datetimepicker.min",
         //时间控件
        "moment": "uplanPlug/daterangepicker/js/moment.min",
        "daterangepicker": "uplanPlug/daterangepicker/js/daterangepicker.min",

        //三级联动
        "jquerycxselect": "uplanPlug/jquerycxselect/jquery.cxselect.min",

        //吸色
        "colorpicker": "uplanPlug/colorpicker/js/colorpicker.min",

        //树
        "ztree": "uplanPlug/ztree/js/jquery.ztree.core.min",
        //带选择框的树
        "ztree_check" : "uplanPlug/ztree/js/jquery.ztree.excheck.min",
        

        //滑动条
        "rangeSlider": "uplanPlug/rangeSlider/js/rangeSlider.min",

        //滚动条
        "nicescroll": "uplanPlug/nicescroll/jquery.nicescroll.min",

         //触摸滑动
        "swiper": "uplanPlug/swiper/js/swiper.min",

        //提示框
        "layer":"uplanPlug/layer/layer.min",

        //grid
        "jqGrid":"uplanPlug/jqGrid/js/jqGrid.min",
        
        //echart
        "echarts": "uplanPlug/echart/echarts"
    },
    map: {
        '*': {
            'css': 'js/css.min'
        }
    },
    //不支持AMD规范使用shim
    shim: {

        'uplan': {
            exports: 'uplan'
        },
        'uskin': {
            deps: ['uplan'],
            exports: 'uskin'
        },

        'jqGrid': {
            deps: ['nicescroll','datetimepicker','layer'],
            exports: 'jqGrid'
        },

        'rangeSlider': {
            deps: ["css!uplanPlug/rangeSlider/css/rangeSlider.min"],
            exports: 'rangeSlider'
        },

        'dragsort': {
            exports: 'dragsort'
        },

        'layer': {
            deps:["css!uplanPlug/layer/skin/layer.min"],
            exports: 'layer'
        },


        'nicescroll': {
            exports: 'nicescroll'
        },

        'ztree': {
            deps: ['css!uplanPlug/ztree/css/zTree.min'],
            exports: 'ztree'
        },

        'datetimepicker': {
            deps: ['uplan','css!uplanPlug/datetimepicker/css/uplan.datetimepicker.min'],
            exports: 'datetimepicker'
        },


        'daterangepicker': {
            deps: ['moment','css!uplanPlug/daterangepicker/css/daterangepicker.min'],
            exports: 'daterangepicker'
        },

        'swiper':{
            deps: ['css!uplanPlug/swiper/css/swiper.min'],
            exports: 'swiper'
        }

    }

});

